    private void btnAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarActionPerformed
        int dato = Integer.parseInt(txtNumero.getText().strip());
        if (l.inicio == null) {
            l.inicio = new NodoCircular(null, dato);
            l.inicio.setSgte(l.inicio);
            l.tamanio++;
        } else {
            NodoCircular aux = l.inicio;
            while(aux.getSgte() != l.inicio) {
                aux = aux.getSgte();
            }
            aux.setSgte(new NodoCircular(l.inicio, dato));
            l.tamanio++;
        }
        txtNumero.setText("");
        txtNumero.requestFocus(); 
    }//GEN-LAST:event_btnAgregarActionPerformed

    private void btnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarActionPerformed
         int dato = Integer.parseInt(JOptionPane.showInputDialog("Dato a eliminar:"));
         NodoCircular aux = l.inicio;
         if (l.tamanio > 0) {
             if (l.tamanio == 1) {
                 if (l.inicio.getDato() == dato) {
                     l.inicio = null;
                     l.tamanio = 0;
                 }
             } else {
//                while(aux.getSgte() != l.inicio) {
//                    if (aux.getSgte().getDato() == dato) {
//                        
//                    }
//                }
                while(l.inicio.getSgte().getDato() != dato) {
                    l.inicio = l.inicio.getSgte();
                    if (l.inicio == aux) {
                        JOptionPane.showMessageDialog(null, "No se encontro el elemento.");
                        return;
                    }
                }
                aux = l.inicio.getSgte();
                l.inicio.setSgte(aux.getSgte());
                l.tamanio--;
             }
         } else {
             JOptionPane.showMessageDialog(null, "No hay elementos.");
         }
    }//GEN-LAST:event_btnEliminarActionPerformed

    private void btnMuestraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMuestraActionPerformed
        NodoCircular aux = l.inicio;
         taResultado.setText("");
         if (l.tamanio > 0) {
             while (aux.getSgte() != l.inicio ) {                 
                 taResultado.append(""+aux.getDato() +"\n");
                 aux = aux.getSgte();
             }
                 taResultado.append(""+aux.getDato() +"\n");
         } else {
            taResultado.setText("No hay elementos."); 
         }
    }//GEN-LAST:event_btnMuestraActionPerformed

